    public boolean onKeyDown(int keyCode, KeyEvent event) { // this is override method
        if(keyCode == KeyEvent.KEYCODE_BACK){
            onBackPressed(); // call the function below
            //return true;
        }
        return false;
    }

    public void onBackPressed() {
        AlertDialog.Builder dialog = new AlertDialog.Builder(v.getContext());//เดิม new AlertDialog.Builder(getView().getContext());
        dialog.setTitle("Exit?"); // set title
        dialog.setMessage("คุณต้องการออกหรือไม่?"); // set message
        dialog.setPositiveButton("ตกลง",
                new DialogInterface.OnClickListener() {
                    @Override
                    public void onClick(DialogInterface dialog, int which) {
                        //FragmentTab1.this.onBackPressed(); // when click OK button, finish current activity!
                        getActivity().finish();
                    }
                });
        dialog.setNegativeButton("ยกเลิก",
                new DialogInterface.OnClickListener() {
                    @Override
                    public void onClick(DialogInterface dialog, int which) {
                        Toast.makeText(getActivity(), "Cancelled", Toast.LENGTH_SHORT).show(); // just show a Toast, do nothing else
                    }
                });
        dialog.create().show();
    }

    @Override
    public View onCreateView(LayoutInflater inflater,ViewGroup container,Bundle saveInstanceStates) {
        v = inflater.inflate(R.layout.menu4_layoutmygps, container, false);
        //==========Coding============================================
        textLat = (TextView) v.findViewById(R.id.textLat);
        textLon = (TextView) v.findViewById(R.id.textLon);
        textUTM = (TextView) v.findViewById(R.id.textUTM);
        textX = (TextView) v.findViewById(R.id.textX);
        textY = (TextView) v.findViewById(R.id.textY);
        Refresh = (Button) v.findViewById(R.id.btnRefresh);
        btnExit = (Button) v.findViewById(R.id.btnExit);

        appLocationService = new AppLocationService(
                getActivity());

        btnGPSShowLocation = (Button) v.findViewById(R.id.btnGPSShowLocation);
        btnGPSShowLocation.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View arg0) {
                Location gpsLocation = appLocationService
                        .getLocation(LocationManager.GPS_PROVIDER);
                if (gpsLocation != null) {
                    double latitude = gpsLocation.getLatitude();
                    double longitude = gpsLocation.getLongitude();
                    String result1 = "Latitude = " + latitude;
                    String result2 = "Longitude = " + longitude;
                    //tvAddress.setText(result);
                    textLat.setText(result1);
                    textLon.setText(result2);
                    Deg2UTM(latitude,longitude);
                } else {
                    showSettingsAlert();
                }
            }
        });

        Refresh.setOnClickListener(new Button.OnClickListener(){
            @Override
            public void onClick(View v) {
                textLat.setText("Lat = ");
                textLon.setText("Lon = ");
                textUTM.setText("UTM:");
                textX.setText("X = ");
                textY.setText("Y = ");
                textLat.requestFocus();
            }
        });

        btnExit.setOnClickListener(new Button.OnClickListener(){
            public void onClick(View v) {
                onBackPressed();
            }
        });
        //=====================================================
        return v;
    }

    public void showSettingsAlert() {
        AlertDialog.Builder alertDialog = new AlertDialog.Builder(
                getActivity());
        alertDialog.setTitle("SETTINGS");
        alertDialog.setMessage("Enable Location Provider! Go to settings menu?");
        alertDialog.setPositiveButton("Settings",
                new DialogInterface.OnClickListener() {
                    public void onClick(DialogInterface dialog, int which) {
                        Intent intent = new Intent(
                                Settings.ACTION_LOCATION_SOURCE_SETTINGS);
                        menu4_FragmentMyGPS.this.startActivity(intent);
                    }
                });
        alertDialog.setNegativeButton("Cancel",
                new DialogInterface.OnClickListener() {
                    public void onClick(DialogInterface dialog, int which) {
                        dialog.cancel();
                    }
                });
        alertDialog.show();
    }

